import numpy as np
import matplotlib.pyplot as plt

# --- Read data from results.txt (generated by C code) ---
data1 = np.loadtxt('results_Y1.txt')  # Each row: X[i]    Y[i]
data2 = np.loadtxt('results_Y2.txt')  # Each row: X[i]    Y[i]

data1_last = data1[-6:]  # Last 6 points for Y1
data2_last = data2[-6:]  # Last 6 points for Y2

x_euler_1 = data1[:, 0]
y_euler_1 = data1[:, 1]
x_euler_2 = data2[:, 0]
y_euler_2 = data2[:, 1]

# --- Compute the exact analytical solution y(x) = (2/3) * x^(3/2) ---
x_exact = np.linspace(min(x_euler_1), max(x_euler_1), 200)  # 200 points for smooth curve
y_exact1 = np.exp(-x_exact) * np.sin(2*x_exact)
y_exact2 = ((2* np.exp(-x_exact) * np.cos(2*x_exact)) - (np.exp(-x_exact) * np.sin(2*x_exact)))

# --- Plot both curves ---
plt.figure(figsize=(8, 6))

# Euler method points
plt.plot(x_euler_1, y_euler_1, 'ro-', label="RK Y1", markersize=5)
plt.plot(x_euler_2, y_euler_2, 'go-', label="RK Y2", markersize=5)

# Exact solution curve
plt.plot(x_exact, y_exact1, 'b-', label="Exact Solution y1(x)", linewidth=2)
plt.plot(x_exact, y_exact2, 'm-', label="Exact Solution y2(x)", linewidth=2)

# --- Graph formatting ---
plt.title("Comparison between RK Methods for Y1, Y2 and Exact Solution", fontsize=13)
plt.xlabel("x", fontsize=12)
plt.ylabel("y(x)", fontsize=12)
plt.legend()
plt.grid(True)

